INSERT INTO public."PizzaProducts" VALUES ('04bc48aa-0fe2-49f2-8aa0-ad266326a586', 'Tuna', 'tuna.png', 14.99, 'The pizza tuna is a savory treat, with melted cheese, spicy tomato sauce, and flaky tuna chunks on a thin crust.', 'Cheese, Tomato sauce, Tuna chunks.');
INSERT INTO public."PizzaProducts" VALUES ('0a99adf4-604a-4aef-9538-12f32bae696b', 'Hawaiian', 'hawaiian.png', 13, 'Experience a tropical taste with a pizza hawaiian style, featuring juicy pineapple chunks, tender ham, and melted mozzarella cheese on a tangy tomato sauce and a fluffy crust.', 'Cheese, Tomato sauce, Ham, Pineapple');
INSERT INTO public."PizzaProducts" VALUES ('0a9a7cef-cee2-46b7-99fc-945976b248d8', 'Black Olives', 'olives.png', 11.5, 'The pizza black olives is a Mediterranean delight, with creamy cheese, garlicky tomato sauce, and briny black olives on a chewy crust.', 'Cheese, Tomato sauce, Black olives, Olive oil');
INSERT INTO public."PizzaProducts" VALUES ('26d1a429-7e12-4a96-ad8d-5be3f7c7b61f', 'Pepperoni', 'pepperoni.png', 14, 'The pizza pepperoni is a meaty delight, with stretchy cheese, rich tomato sauce, and spicy pepperoni slices on a golden crust.', 'Cheese, Tomato sauce, Pepperoni, Olive oil');
INSERT INTO public."PizzaProducts" VALUES ('48819716-9763-4ed1-af5b-98a6eb853834', 'Italian', 'italianham.png', 17.99, 'The classical Italian pizza is a simple and authentic dish, with crispy dough, fresh tomato sauce, fragrant basil, and soft mozzarella cheese.', 'Cheese, Tomato sauce, Italian smoked Ham, Basil');
INSERT INTO public."PizzaProducts" VALUES ('72322eaa-df81-40a4-8dc3-09433f5dbb9e', 'Veggie', 'veggie.png', 11.99, 'The pizza veggie is a colorful feast, with gooey cheese, fresh tomato sauce, and crunchy vegetables on a fluffy crust.', 'Cheese, Tomato sauce, Peppers, Mushrooms, Onions, Olives, Spinach, Broccoli, Artichokes');
INSERT INTO public."PizzaProducts" VALUES ('7cad4814-c56a-49f3-baca-933cf0ee931c', 'BBQ', 'barbecue.png', 14.5, 'Sink your teeth into a delicious barbecue pizza with smoky chicken, sweet corn, red onion, and melted cheddar cheese on a soft and chewy crust.', 'Cheese, Tomato sauce, Chicken,Corn, Red Onions');
INSERT INTO public."PizzaProducts" VALUES ('a07498ed-697c-4dfd-83f2-224dfe1456f2', 'Calzone', 'calzone.png', 14, 'Indulge in a warm and cheesy calzone filled with spicy pepperoni, fresh spinach, ricotta cheese, and marinara sauce, wrapped in a golden-brown dough.', 'Cheese, Tomato sauce, Ham, Pepperoni, Spinach');
INSERT INTO public."PizzaProducts" VALUES ('b526c671-95d8-4275-bfb8-b9aab5dcc5c2', '4 Cheeses', 'cheese4.png', 17.99, 'Treat yourself to a pizza 4 cheese with a rich and creamy blend of mozzarella, parmesan, gorgonzola, and provolone cheese on a crispy crust, sprinkled with fresh basil and oregano.', 'Mozzarella, Parmesan, Gorgonzola, Provolone cheese, Cream sauce');
INSERT INTO public."PizzaProducts" VALUES ('bac25db1-3cf4-47ef-8de7-220219f05365', 'Green Pesto', 'greenpesto.png', 12.7, 'Savour a pizza green pesto with a fragrant and nutty sauce made from basil, pine nuts, garlic, and olive oil, topped with sliced tomatoes, mozzarella cheese, and black olives.', 'Cheese, Tomato sauce, Green Basil, Olives');
INSERT INTO public."PizzaProducts" VALUES ('d620e337-16a6-4e9a-8288-957e71afcbc7', 'Anchovies', 'anchovies.png', 15.99, 'Enjoy a crispy thin crust pizza topped with tangy tomato sauce, gooey mozzarella cheese, and salty anchovies for a burst of flavour in every bite.', 'Cheese, Tomato sauce, anchovies');
INSERT INTO public."PizzaProducts" VALUES ('dc48a7ef-4d35-429e-a758-ae5b9dd3488b', 'Salmon', 'salmon.png', 18.5, 'The pizza salmon is a scrumptious delight, with creamy cheese, tangy tomato sauce, and succulent salmon slices on a crispy crust.', 'Cheese, Tomato sauce, Salmon slices.');